import React from 'react';
import './App.css';


class App extends React.Component{
  constructor(){
    super()
    this.state={
      searchvalue:"",
    }
    this.handlechange=this.handlechange.bind(this)
    this.handleclick=this.handleclick.bind(this)
  }

handlechange(e){
    this.setState({searchvalue:e.target.value})
}

handleclick(){
alert(this.state.searchvalue)
}

  render(){
    return(<><h1 id="heading">Weather App</h1><div id="main-div"><div id="searchbar-div">
      <input type="text" placeholder="Search City" id="searchbar" onChange={this.handlechange}></input>
      <button id="search-button" onClick={this.handleclick}>Search</button>
      </div></div></>)
  }
}


export default App;
