import React from 'react';
import './App.css';


class App extends React.Component{
  constructor(){
    super()
    this.state={
      searchvalue:"",
      loadingmessage:"",
      info:null
    }
    this.handlechange=this.handlechange.bind(this)
    this.handleclick=this.handleclick.bind(this)
      this.fetchdata=this.fetchdata.bind(this)
  }

handlechange(e){
    this.setState({searchvalue:e.target.value})
}

handleclick(){
alert(this.state.searchvalue)
}

fetchdata(){
  this.setState({loadingmessage:"Loading Data ..."})
    fetch('https://api.openweathermap.org/data/2.5/weather?q=mumbai&APPID=20480b401406258c390ff1cf3600d60a')
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({loadingmessage:""})
        this.setState({info:result})
          console.log(this.state.info)
      },
      (error) => {
        this.setState({loadingmessage:"Erorr Loading Data ..."})
       console.log("error")

      }
    )
}


  render(){
    let d="";
    if(this.state.info!=null){
      d=
     <p><p>Weather : {this.state.info.weather[0].main}</p>
     <p>Wind : {this.state.info.wind.speed}</p>
     <p>Humidity : {this.state.info.main.humidity}</p>
     <p>Pressure  : {this.state.info.main.pressure}</p>
     <p>Max Temp  : {this.state.info.main.temp_max}</p>
     <p>Min Temp : {this.state.info.main.temp_min}</p>
     <p>Sunrise  : {this.state.info.sys.sunrise}</p>
     <p>Sunset  : {this.state.info.sys.sunset}</p>
     </p>
    }
    return(<><h1 id="heading">Weather App</h1><div id="main-div"><div id="searchbar-div">
      <input type="text" placeholder="Search City" id="searchbar" onChange={this.handlechange}></input>
      <button id="search-button" onClick={this.fetchdata}>Search</button>
      </div><div id="weather-div">{this.state.loadingmessage}</div>
     {d}
      </div></>)
  }
}


export default App;
