import React from 'react';
import './App.css';
import CommentComponent from './CommentComponent'

class App extends React.Component{
  constructor(){
    super()
    this.state={
      searchvalue:"",
      loadingmessage:"",
      info:null,
      found:"",
    }
    this.handlechange=this.handlechange.bind(this)
    this.handleclick=this.handleclick.bind(this)
      this.fetchdata=this.fetchdata.bind(this)
  }

handlechange(e){
    this.setState({searchvalue:e.target.value})
}

handleclick(city){
  if(city!=="")
this.fetchdata(city)
else
alert("Enter a City Name")
}

fetchdata=(city)=>{
  this.setState({loadingmessage:"Loading Data ..."})
    fetch('https://api.openweathermap.org/data/2.5/weather?q='+city+'&APPID=20480b401406258c390ff1cf3600d60a')
    .then(res => res.json())
    .then(
      (result) => {
       if(result.cod!==200){
       this.setState({found:"no"})
      this.setState({loadingmessage:"City Not Found"})
      }
       if(result.cod===200){
       this.setState({found:"yes"})
      }
       this.setState({loadingmessage:""})
        this.setState({info:result})
          console.log(this.state.info)
      },
      (error) => {
        this.setState({loadingmessage:"Erorr Loading Data ..."})
       console.log("error")

      }
    )
}


  render(){
    var date=new Date();
    let d="";
    if(this.state.info!==null){
      if(this.state.info.cod!=="404"){
      d=<div id="sbkabap-div"><h5>{this.state.info.name} , {this.state.info.sys.country} ,{date.getTime()}</h5>
      <div id="second-div">
        <div id="p">{(this.state.info.main.temp-273).toFixed(2)} &#176; C   ~</div>
        <div id="q">
     <p>Weather : {this.state.info.weather[0].main}
     <br/>Wind : {this.state.info.wind.speed}</p>
     <p>Humidity : {this.state.info.main.humidity}
     <br/>Pressure  : {this.state.info.main.pressure}</p>
     <p>Max Temp  : {this.state.info.main.temp_max}
     <br/>Min Temp : {this.state.info.main.temp_min}</p>
     <p>Sunrise  : {this.state.info.sys.sunrise}
     <br/>Sunset  : {this.state.info.sys.sunset}</p>
     </div></div>
     <CommentComponent/>
     </div>
    }}

    if(this.state.found==="no")
    {
      return(<><h1 id="heading">Weather App</h1><div id="main-div"><div id="searchbar-div">
      <input type="text" placeholder="Search City" id="searchbar" onChange={this.handlechange}></input>
      <button id="search-button" onClick={()=>this.handleclick(this.state.searchvalue)}>Search</button>
      </div><div className="weather-div">City Not Found</div>
      </div></>)
    }
    else{
    return(<><h1 id="heading">Weather App</h1><div id="main-div"><div id="searchbar-div">
      <input type="text" placeholder="Search City" id="searchbar" onChange={this.handlechange}></input>
      <button id="search-button" onClick={()=>this.handleclick(this.state.searchvalue)}>Search</button>
      </div><div className="weather-div">{this.state.loadingmessage}</div>
     {d}
      </div>
      </>)}
  }
}


export default App;
